/**
 * Clase fuerza, tiene un unico
 * atributo, su valor numérico
 */
export class Fuerza {
  readonly valor: number;

  constructor(valor: number) {
    this.valor = valor;
  }

  /**
   * Método que obtiene el
   * objeto
   * @returns el objeto
   */
  get_object(): this {
    return this;
  }

  /**
   * Método que obtiene el valor
   * del atributo
   * @returns "number"
   */
  get_number(): number {
    return this.valor;
  }

  /**
   * Convierte de newtons a kilopondios
   * @returns el valor * 0.101972
   */
  convert(): number {
    return (this.valor * 0.101972);
  }
}

/**
 * Clase longitud, trabaja solamente
 * con un único atributo
 */
export class Longitud {
  readonly valor: number;

  constructor(valor: number) {
    this.valor = valor;
  }

  /**
   * Obtiene el objeto de la clase
   * @returns el propio objeto
   */
  get_object(): this {
    return this;
  }

  /**
   * Retorna el valor del atributo
   * @returns el valor del atributo
   */
  get_number(): number {
    return this.valor;
  }

  /**
   * Convierte de pulgadas a centimetros
   * @returns el valor * 2.54
   */
  convert(): number {
    return (this.valor * 2.54);
  }
}

/**
 * Clase masa, tiene un único
 * atributo, su valor
 */
export class Masa {
  readonly valor: number;

  constructor(valor: number) {
    this.valor = valor;
  }
  /**
   * Método que obtiene el objeto en sí
