/**
 * Se importan las clases, y la propia interfaz
 */
import {Fuerza} from './fuerza';
import {isConvertible} from './isConvertible';
import {Longitud} from './longitud';
import {Masa} from './masa';
import {Temperatura} from './temperatura';
import {Tiempo} from './tiempo';
import {Velocidad} from './velocidad';
import {Volumen} from './volumen';

/**
 * Esta es la clase genérica, de antemano no sabemos cual es la clase
 * que extenderá, por eso ponemos todas, tiene que implementar los métodos de
 * la interfaz isConvertible, en el constructor se crea un objeto de tipo T,
 * y se iguala
 */
export class Magnitud<T extends Velocidad | Longitud | Tiempo | Temperatura | Fuerza | Volumen | Masa> implements isConvertible<T> {
  objeto: T;

  /**
   * Es un objeto de tipo T, que puede
   * ser velocidad, masa...
   * @param objeto
   */
  constructor(objeto: T) {
    this.objeto = objeto;
  }

  /**
   * Este método accede al objeto al que esta extendiendo
   * @returns Devuelve el objeto
   */
  get_object(): T {
    return this.objeto;
  }

  /**
   * Este método accede al método de la clase instanciada
   * por ejemplo, al get_number, de masa, velocidad...
   * @returns el valor numérico del atríbuto valor
   */
  get_number(): number {
    return this.objeto.get_number();
  }

  /**
   * Método que accede al convert de la clase
   * instanciada
   * @returns el resultado de la operación
   * convert() al objeto de tipo T
   */
  convert(): number {
    return this.objeto.convert();
  }
}

const velocidad_1: Velocidad = new Velocidad(5);
const longitud_1: Longitud = new Longitud(5);
const tiempo_1: Tiempo = new Tiempo(5);
const temperatura_1: Temperatura = new Temperatura(5);
const fuerza_1: Fuerza = new Fuerza(5);
const volumen_1: Volumen = new Volumen(5);
const masa_1: Masa = new Masa(5);

const magnitud_1: Magnitud<Velocidad> = new Magnitud(velocidad_1);
console.log(magnitud_1.convert());

const magnitud_2: Magnitud<Longitud> = new Magnitud(longitud_1);
console.log(magnitud_2.convert());

const magnitud_3: Magnitud<Tiempo> = new Magnitud(tiempo_1);
console.log(magnitud_3.convert());

const magnitud_4: Magnitud<Temperatura> = new Magnitud(temperatura_1);
console.log(magnitud_4.convert());

const magnitud_5: Magnitud<Fuerza> = new Magnitud(fuerza_1);
console.log(magnitud_5.convert());

const magnitud_6: Magnitud<Volumen> = new Magnitud(volumen_1);
console.log(magnitud_6.convert());

const magnitud_7: Magnitud<Masa> = new Magnitud(masa_1);
console.log(magnitud_7.convert());

/**
 * Clase que representa a un documental
 */
export class Documental {
  /**
   * Dispone de dos atributos,uno
   * es un string y el otro es un
   * número, el nombre y la fecha
   */
  private nombre: string;
  private fecha: number;

  constructor(nombre: string, fecha: number) {
    this.nombre = nombre;
    this.fecha = fecha;
  }

  /**
   * Tenemos un método para obtener
   * el nombre porque éste es privado
   * @returns el nombre, un string
   */
  get_nombre(): string {
    return this.nombre;
  }

  /**
   * Tenemos un método para obtener
   * la fecha porque ésta es privada
   * @returns la fecha, un número
   */
  get_fecha(): number {
    return this.fecha;
  }
}

/**
 * Importamos la clase documental y la interfaz
 */
import {Documental} from './documentales';
import {BasicStreamableCollection} from './basicStreamableCollection';

/**
 * Clase que representa a un conjunto de documentales,extiende a la
 * clase generica que tiene a los vectores de T
 */
export class Coleccion_Documentales extends BasicStreamableCollection<Documental> {
  constructor(objetos: Documental[]) {
    super(objetos);
  }

  /**
   * Realiza una búsqueda sobre el vector, a través de un parámetro
   * @param fecha Un valor numérico
   * @returns Un vector de documentales, o un vector vacío
   */
  obtenerObjetosPorFecha(fecha: number): Documental[] {
    return this.objetos.filter((objeto: Documental) => objeto.get_fecha() === fecha);
  }

  /**
   * Realiza una búsqueda sobre el vector, a través de un parámetro
   * @param nombre Un valor de caracteres
   * @returns Un vector de documentales, o un vector vacío
   */
  obtenerObjetosPorNombre(nombre: string): Documental[] {
    return this.objetos.filter((objeto: Documental) => objeto.get_nombre() === nombre);
  }
}

const coleccion: Coleccion_Documentales = new Coleccion_Documentales([
  new Documental('Documental de la 1', 1970),
  new Documental('Documental de Doscovery Chanel', 1980),
  new Documental('Documental del canal de historia', 2001),
]);

console.log(coleccion.obtenerNumeroObjetos());
coleccion.añadirObjeto(new Documental('Documental de Paramount Channel', 1980));
console.log(coleccion.obtenerNumeroObjetos());

console.log(coleccion.obtenerObjetosPorFecha(1980));
console.log(coleccion.obtenerObjetosPorNombre('Documental de la 1'));

/**
 * Importamos la interfaz SearchStreamable que
 * extiende a la primera, streamable, se encuentra
 * en el mismo directorio
 */
import {SearchStreamable} from './streamable';

/**
 * Clase abstracta que implementa parte de las
 * funcionalidades de la interfaz, las subclases
 * implementarán las funciones de búsqueda
 */
export abstract class BasicStreamableCollection<T> implements SearchStreamable<T> {
  constructor(protected objetos: T[]) {};

  /**
   * Método que inserta un nuevo objeto en el vector T
   * @param nuevo_Objeto el objeto que se va a insertar
   */
  añadirObjeto(nuevo_Objeto: T) {
    this.objetos.push(nuevo_Objeto);
  }

  /**
   * Método que obtiene el número de elementos del
   * vector T, que puede ser peliculas, series...
   * @returns el tamaño del vector
   */
  obtenerNumeroObjetos() {
    return this.objetos.length;
  }

  /**
   * La implementa la subclase
   * @param fecha Un valor numérico
   */
  abstract obtenerObjetosPorFecha(fecha: number): T[];

  /**
   * La implementa la subclase
   * @param nombre Un valor de caracteres
   */
  abstract obtenerObjetosPorNombre(nombre: string): T[];
}

/**
 * Clase que representa a una película, tiene
